class Gen
{
    int num_letras;
    String cadena;
    static void Encuentra_IRNA(Gen secuencia2)
    {
        Matriz_dinamica matrices=new Matriz_dinamica(1, 27, secuencia1, secuencia2);
        matrices.busqueda_general();
        matrices.encuentra_mejor_opcion_izquierda();
        matrices.resultado();
    }

    void posicion_iRNA(int [] posición, String iRNA)
    {
        int    i, indice_posición=0, letra=0, n_letras=0;
        String gen_iRNA="";
        System.out.println(" cadena:" + iRNA + " posición[indice_posición]:" + posición[indice_posición]);
        for (i=0; i < num_letras; i++)
        {
            if (i == posición[indice_posición])
            {
                gen_iRNA=gen_iRNA + "---";
                n_letras++;
            }
            if (n_letras < iRNA.length & n_letras > 0)
            {
                if (iRNA.charAt(n_letras - 1) == cadena.charAt(i))
                    gen_iRNA=gen_iRNA + cadena.charAt(i);
                else
                    gen_iRNA=gen_iRNA + Character.toLowerCase(cadena.charAt(i));
                n_letras++;
                if (n_letras == 27)
                {
                    n_letras=0;
                    if (indice_posición < posición.length - 1)
                        indice_posición++;
                    gen_iRNA=gen_iRNA + "---";
                }
            }
            else
                gen_iRNA=gen_iRNA + cadena.charAt(i);
            //System.out.println(" cadena:"+gen_iRNA);
        }
        System.out.println("GEN:" + gen_iRNA);
    }

    public static void main(String args[])
    {
        Gen secuencia1=new Gen();
        Gen secuencia2=new Gen();
        secuencia1.cadena    ="";
        secuencia2.cadena    ="";
        secuencia1.num_letras=secuencia1.cadena.length();
        secuencia2.num_letras=secuencia2.cadena.length();
        Encuentra_IRNA(secuencia1, secuencia2);
    }
}
